        sorted_keyframes = sorted(keyframe_files, key=lambda x: int(x.replace('keyFrame.', '')))
        if len(sorted_keyframes) >= 3:
            frames_to_download = {sorted_keyframes[0], sorted_keyframes[-1], sorted_keyframes[len(sorted_keyframes) // 2]}
