def test_compute_crc64_none():
    with pytest.raises(TypeError):
        crc64.compute_crc64(None, 0)

    with pytest.raises(TypeError):
        crc64.compute_crc64(b'', None)

def test_compute_crc64():
    for data, initial, expected in tests:
        actual = crc64.compute_crc64(data, initial)
        assert actual == expected

def test_compute_crc64_chunks():
    data = os.urandom(4 * 1024 * 1024)
    chunk_size = 1024 * 1024
    full_crc = crc64.compute_crc64(data, 0)

    index = 0
    crc = 0
    while index < len(data):
        end = index + chunk_size
        crc = crc64.compute_crc64(data[index:end], crc)
        index = end

    assert crc == full_crc
